
public class Nesting
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		
		
		for (int e= 0; e < 19; e++){
			
			for (int a= 0; a < 19; a++){
				if ((a<=9-e||a>=9+e||a==0||a==18||e==18|| 6<a&&a<12&&e>=13|| 2<a&&a<5&&(e==12||e==13)||13<a&&a<16&&(e==12||e==13))){
				System.out.print("* " );	
				}
				else {
	               System.out.print("  ");
				}
		   
	    
			}
		
		System.out.println();
		
		}
		
		

	}

}
